const { useState } = require('react');

const Login = () => {
  const [users, setUser] = useState([])
  const [email, setEmail] = useState('')
  const [password, setPassword] = useState('')
  const [confirmPassword, setConfirmPassword] = useState('')
  const validatAdd = () => {
    if (email.trim.length < 1){
      return ' Email must be At least 1 character!'
    }else if ( password !== confirmPassword) {
      return ' password must be equal confirmPassword!'
    }
    setUser((prev => [...prev, {
      email, 
      password,
      id: users.length+1,
    }]))
  }
  return <>
    <div className="form">
      <label>Email</label>
      <input type="text" className="email" onChange={(e) => setEmail(e.target.value)}/>
      <label>Password</label>
      <input type="text" className="password" onChange={(e) => setPassword(e.target.value)} />
      <label>Confirm password</label>
      <input type="text" className="confirmPassword" onChange={(e) => setPassword(e.target.value)} />
      <button onClick={() => validatAdd}>Add</button>
      {users.length ? users.map((user) => {
      return <div>
        Email: user.email
      </div>
      }): null}</div>
  </>
}

export default Login;
