import { useState } from 'react'

const CounterCom = () => {
  const [count, incrementCount] = useState((prev) => prev);
  const [dCount, decrementCount] = useState((prev) => prev);
  const [rCount, ResetCount] = useState((prev => prev || 0));
  const  handelIncrementCount = () => incrementCount(rCount+1)
  const  handelDecrementCount = () => decrementCount(rCount-1)
  const  handelResetCount = () => ResetCount(0)
  return (
    <div>
      <h2>count {rCount}</h2>
      <button onClick={() => handelIncrementCount()}>Increment</button>
      <button onClick={() => handelDecrementCount()} >Decrement</button>
      <button onClick={() => handelResetCount()}>Reset</button>
    </div>
  )
}

export default CounterCom;
