const { useState } = require('react');

const Login = () => {
  const [error, setError] = useState
  const [users, setUser] = useState([])
  const [email, setEmail] = useState('')
  const [password, setPassword] = useState('')
  const [confirmPassword, setConfirmPassword] = useState('')
  const setErr = (err) => <p>{err}</p>
  const validatAdd = () => {
    if (email.trim.length < 1){
      return setErr(' Email must be At least 1 character!')
    }else if ( password !== confirmPassword) {
      return setErr('password must be equal confirmPassword!')
    }
    setUser((prev => [...prev, {
      email, 
      password,
      id: users.length+1,
    }]))
  }
  return <>
    <div className="form">
      <label>Email</label>
      <input type="text" className="email" onChange={(e) => setEmail(e.target.value)}/>
      <label>Password</label>
      <input type="text" className="password" onChange={(e) => setPassword(e.target.value)} />
      <label>Confirm password</label>
      <input type="text" className="confirmPassword" onChange={(e) => setPassword(e.target.value)} />
      <button onClick={() =>  validatAdd}>Add</button>
      {users.length ? users.map((user) => {
      return <div>
        <p>Email: {user.email}</p>
        <p>Password: {user.password}</p>
        <button>delete</button>
        <hr/>
      </div>
      }): <p>{() => setError}</p>}</div>
  </>
}

export default Login;
