import { useState } from 'react'



const Second = () => {
  const colors = ['red', 'blue', 'green', 'black', 'yellow'];
  const [colorN, setColor] = useState(0);
  const handleClassName = () => setColor((prev) => colorN +1 )
  const restColor = () => setColor(0)
  return <div className={colors[colorN]}>
    {/* <button onClick={colors.length < colorN ? () =>handleClassName() : () => restColor()}>color</button> */}
    <button onClick={() =>handleClassName() && restColor}>color</button>
  </div>
}


export default Second;
